mapper.insert:
INSERT INTO board (board_title, board_content, mno, board_ip) 
VALUES (#{title}, #{content}, (SELECT mno FROM member WHERE mid=#{mid}), #{ip});

	SELECT LAST_INSERT_ID()

mapper.select:
SELECT cno, ccomment, cdate, clike, mid, mname, cip, profile 
FROM commentview 
WHERE board_no=#{no}
